# Массив (список)numbers = [10, 20, 30, 40, 50]# Добавление элементаnumbers.append(60)# Доступ к элементамprint(f"Элемент с индексом 3: {numbers[3]}")# Размер массиваprint(f"Размер массива: {len(numbers)}")# Срезыprint(f"Срез [2:5]: {numbers[2:5]}")# Стеки# Использование списка как стекаstack = []# Добавление элементовstack.append("первый")stack.append("второй")stack.append("третий")# Просмотр верхнего элементаprint(f"Верхний элемент стека: {stack[-1]}")# Удаление элементаstack.pop()# Проверка на пустотуprint(f"Стек пустой? {'Да' if len(stack) == 0 else 'Нет'}")# Размер стекаprint(f"Размер стека: {len(stack)}")